import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '../styles/Input.css';
import '../styles/ColorScheme.css';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const propTypes = {
    isDate: PropTypes.bool,
    onChange: PropTypes.func,
    placeholder: PropTypes.string
};

const defaultProps = {
    isDate: false,
    onChange: () => {},
    placeholder: ''
};

function Input(props) {
    const {
        isDate,
        onChange,
        placeholder
    } = props;

    const [selectedDate, setSelectedDate] = useState(new Date().toLocaleDateString("en-US"));

    function handleDateChange(date) {
        setSelectedDate(date);
        onChange(date);
    }

    if (isDate) {
        return (
            <DatePicker className="input-field" selected={selectedDate} onChange={handleDateChange} />
        )
    }
    return (
        <input className="input-field" placeholder={placeholder} onChange={onChange} />
    );
}

Input.propTypes = propTypes;
Input.defaultProps = defaultProps;
export default Input;
