import React, { useState } from 'react';
import Button from './Button';
import Input from './Input';
import '../styles/ColorScheme.css';
import '../styles/VoterRegistration.css';
import logo from '../assets/logos/logo.png';

function VoterRegistration() {
    const [isReadySubmit, setIsReadySubmit] = useState(false);
    const [name, setName] = useState("");
    const [dob, setDOB] = useState("");
    const [ssn, setSSN] = useState("");

    function handleTextChange(event) {
        console.log(event);
        if (event.target.placeholder === "First and Last Name") {
            setName(event.target.value);
        } else if (event.target.placeholder === "Date of Birth") {
            setDOB(event.target.value);
        } else if (event.target.placeholder === "Social Security Number") {
            setSSN(event.target.value);
        }

        setIsReadySubmit(name && dob && ssn);
    }

    function handleDateChange(date) {
        setDOB(date.toLocaleDateString());
        setIsReadySubmit(name && dob && ssn);
    }

    return (
        <div className="voter-registration-container">
            <div className="voter-registration-logo-container">
                <img className="voter-registration-logo" src={logo} alt="ElectWise" />
            </div>
            <div className="voter-registration-input-container">
                <Input placeholder="First and Last Name" onChange={handleTextChange} />
            </div>
            <div className="voter-registration-date-container">
                <Input placeholder="Date of Birth" onChange={handleDateChange} isDate={true} />
            </div>
            <div className="voter-registration-input-container">
                <Input placeholder="Social Security Number" onChange={handleTextChange} />
            </div>
            <div className="voter-registration-submit-container">
                <div className="voter-registration-submit">
                    <Button text="Submit" enabled={isReadySubmit} variant="bg-primary-outline" />
                </div>
            </div>
        </div>
    );
}

export default VoterRegistration;
