import React, { useContext } from 'react';

import VoterContext from '../../VoterContext';
import ElectionList from '../Voter/ElectionList';
import ElectionBallot from '../Voter/ElectionBallot';

import '../../styles/ColorScheme.css';
import '../../styles/VoterDashboard.css';

function VoterDashboard(props) {
    const {
        elections,
        electionsLoaded,
        isLoadingElections,
        selectedElection
    } = props;

    const { voterState, voterDispatch } = useContext(VoterContext);

    // Request election data
    // Simple approach until we have backend to talk to
    if (!electionsLoaded) {
        voterDispatch({ type: "attempLoadElections" });
    }

    function renderContents() {
        if (selectedElection) {
            return (<ElectionBallot />);
        } 
        return (<ElectionList isLoading={isLoadingElections} elections={voterState.elections} />);
    }

    return (
        <div className="voter-dashboard-container">
            {renderContents()}
        </div>
    );
}

export default VoterDashboard;
