import React from 'react';
import PropTypes from 'prop-types';

import Button from '../Common/Button';

import '../../styles/ColorScheme.css';
import '../../styles/ElectionModal.css';

const propTypes = {
    election: PropTypes.object,
    onClose: PropTypes.func,
    show: PropTypes.bool
};

const defaultProps = {
    election: {options: []},
    onClose: () => {},
    show: false
}

function ElectionModal(props) {
    const {
        election,
        onClose,
        show
    } = props;

    const modalVisibility = show ? "visible" : "hidden";
    const animation = show ? " animate-in" : "";

    const {
        title,
        totalVotes,
        opens,
        closes,
        options
    } = election;

    function renderOptions() {
        if (!options) return;
        const optionItems = options.map(option => {
            return (
                <div className="election-modal-option">
                    {option.value}
                </div>
            );
        });
        return optionItems;
    }

    return (
        <div className={"election-modal-container"} style={{ visibility: modalVisibility }}>
            <div className={"election-modal" + animation}>
                <div className="election-modal-title">
                    {title}
                </div>
                Make your selection:
                <div className="election-modal-options-container">
                    {renderOptions()}
                </div>
                <Button text="Cancel" onClick={onClose} variant="bg-error" />
            </div>
        </div>
    );
}

function SelectableItem(props) {
    const {
        onClick,
        selected,
        text
    } = props;

    const variant = selected ? " selected" : " not-selected"

    return (
        <div className={"selectable-item-container" + variant} onClick={() => onClick}>
            {text}
        </div>
    )
}

ElectionModal.propTypes = propTypes;
ElectionModal.defaultProps = defaultProps;
export default ElectionModal;
