import React from 'react';
import PropTypes from 'prop-types';

import ReChartPieChart from '../Common/ReChartPieChart';
import { FiActivity, FiBookOpen, FiBook } from 'react-icons/fi';

import '../../styles/ElectionModal.css';
import '../../styles/ElectionModalSelectionPage.css';

const propTypes = {
    election: PropTypes.object,
    onOptionClick: PropTypes.func,
    options: PropTypes.array,
    selectedOption: PropTypes.number
};

const defaultProps = {
    election: {},
    onOptionClick: () => {},
    options: [],
    selectedOption: 0
};

function ElectionModalSelectionPage(props) {
    const {
        election,
        onOptionClick,
        options,
        selectedOption
    } = props;

    const {
        title,
        totalVotes,
        opens,
        closes
    } = election;

    console.log(selectedOption);

    function renderOptions(selectedOption) {
        if (!options) return;
        const optionItems = options.map(option => {
            return (
                <SelectableItem 
                    onClick={() => onOptionClick(option.key)}
                    key={option.key} 
                    text={option.value} 
                    selected={option.key === selectedOption ? true : false}
                />
            );
        });
        return optionItems;
    }

    return (
        <div className="election-modal-selection-page-container">
            <div className="election-modal-information-container">
                <div className="election-modal-category-title">
                    Information
                </div>
                <div className="election-modal-election-title">
                    {title}
                </div>
                <div className="election-modal-election-detail">
                    <FiActivity /> Votes: {totalVotes}
                </div>
                <div className="election-modal-election-detail">
                    <FiBookOpen /> Opens: {opens}
                </div>
                <div className="election-modal-election-detail">
                    <FiBook /> Closes: {closes}
                </div>
            </div>
            <div className="election-modal-statistics-container">
                <div className="election-modal-category-title">
                    Statistics
                </div>
                <div className="election-modal-pie-chart-container">
                    <ReChartPieChart
                        data={election.results}
                        colors={['#0088FE', '#00C49F', '#FFBB28', '#FF8042']}
                    />
                </div>
            </div>
            <div className="election-modal-options-container">
                <div className="election-modal-category-title">
                    Selection
                </div>
                {renderOptions(selectionOption)}
            </div>
        </div>
    )
}

function SelectableItem(props) {
    const {
        key,
        onClick,
        selected,
        text
    } = props;

    const variant = selected ? " selected" : " not-selected"

    return (
        <div className={"selectable-item-container" + variant} onClick={() => onClick(key)}>
            {text}
        </div>
    )
}


ElectionModalSelectionPage.propTypes = propTypes;
ElectionModalSelectionPage.defaultProps = defaultProps;
export default ElectionModalSelectionPage;
