import React, { useState } from 'react';
import PropTypes from 'prop-types';

import ElectionBallotReceipt from './ElectionBallotReceipt';
import Button from '../Common/Button';
import ReChartPieChart from '../Common/ReChartPieChart';
import { FiActivity, FiBookOpen, FiBook } from 'react-icons/fi';

import '../../styles/ColorScheme.css';
import '../../styles/ElectionBallot.css';

const propTypes = {
    election: PropTypes.object,
    onClose: PropTypes.func,
    show: PropTypes.bool
};

const defaultProps = {
    election: {options: []},
    onClose: () => {},
    show: false
}

function ElectionBallot(props) {
    const {
        election,
        onClose,
        show
    } = props;

    const {
        title,
        totalVotes,
        opens,
        closes,
        options,
        categories
    } = election;

    const [selectedOptions, setSelectedOptions] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const animation = show ? "animate-in" : "";

    function renderOptionCategories() {
        const optionCategories = categories.map((category, i) => {
            return (
                <div className="election-ballot-option-category-container">
                    <div className="election-ballot-category-title">
                            Selection for {category}
                        </div>
                    <div className="election-ballot-options-container">
                        {renderOptions(i, selectedOption)}
                    </div>
                </div>
            );
        });
        return optionCategories;
    }

    function renderOptions(category, selectedOption) {
        const relevantOptions = [...options].filter(item => {
            return item.category === category;
        });

        const optionItems = relevantOptions.map(option => {
            return (
                <SelectableItem 
                    onClick={() => setSelectedOption(option.key)}
                    key={option.key} 
                    text={option.value} 
                    selected={(option.key === selectedOption && option.category === category) ? true : false}
                />
            );
        });
        return optionItems;
    }

    return (
        <>
            <ElectionBallotReceipt 
                election={election}
                onClose={() => setShowModal(false)}
                selectedOption={selectedOption} 
                show={showModal}
            />
            <div className={`election-ballot-container ${animation}`}>
                <div className="election-ballot-information-container">
                    <div className="election-ballot-category-title">
                        Information
                    </div>
                    <div className="election-ballot-election-title">
                        {title}
                    </div>
                    <div className="election-ballot-information-row">
                        <div className="election-ballot-election-detail">
                            <FiActivity /> Votes: {totalVotes}
                        </div>
                        <div className="election-ballot-election-detail">
                            <FiBookOpen /> Opens: {opens}
                        </div>
                        <div className="election-ballot-election-detail">
                            <FiBook /> Closes: {closes}
                        </div>
                    </div>
                </div>
                {/* <div className="election-ballot-statistics-container">
                    <div className="election-ballot-category-title">
                        Statistics
                    </div>
                    <div className="election-ballot-pie-chart-container">
                        <ReChartPieChart
                            data={election.results}
                            colors={['#0088FE', '#00C49F', '#FFBB28', '#FF8042']}
                        />
                    </div>
                </div> */}
                {/* <div className="election-ballot-options-container">
                    <div className="election-ballot-category-title">
                        Selection
                    </div>
                    {renderOptions(selectedOption)}
                </div> */}
                {renderOptionCategories()}
                <div className="election-ballot-footer-container">
                    <div className="election-ballot-footer-step-container">
                        Step 1 of 2
                    </div>
                    <div className="election-ballot-footer-buttons-container">
                        <div className="election-ballot-footer-button-container">
                            <Button text="Cancel" onClick={onClose} variant="bg-error" size="lg" />
                        </div>
                        <div className="election-ballot-footer-button-container">
                            <Button
                                text="Continue"
                                onClick={() => setShowModal(true)}
                                variant="bg-primary"
                                size="lg"
                                enabled={selectedOption === null ? false : true}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

function SelectableItem(props) {
    const {
        key,
        onClick,
        selected,
        text
    } = props;

    const variant = selected ? " selected" : " not-selected"

    return (
        <div className={"selectable-item-container" + variant} onClick={() => onClick(key)}>
            {text}
        </div>
    )
}

ElectionBallot.propTypes = propTypes;
ElectionBallot.defaultProps = defaultProps;
export default ElectionBallot;
