import React, { useReducer } from 'react';

import { VoterReducer } from '../../VoterReducer';
import ElectionList from '../Voter/ElectionList';
import ElectionBallot from '../Voter/ElectionBallot';

import '../../styles/ColorScheme.css';
import '../../styles/VoterDashboard.css';

function VoterDashboard() {
    const initialState = {
        elections: [],
        electionsLoaded: false,
        isLoadingElections: false,
        selectedElection: null
    };

    const [state, dispatch] = useReducer(VoterReducer, initialState);

    // Request election data
    // Simple approach until we have backend to talk to
    if (!state.electionsLoaded) {
        dispatch({ type: "attempLoadElections" });
    }

    console.log(state);

    function handleElectionClick(election) {
        console.log("here");
        console.log(election);
        dispatch({ type: "openBallot", action: election });
    }

    function renderContents() {
        if (state.selectedElection) {
            return (<ElectionBallot />);
        } 
        return (
            <ElectionList 
                isLoading={state.isLoadingElections}
                elections={state.elections} 
                onElectionClick={(election) => handleElectionClick(election)} 
            />
        );
    }

    return (
        <div className="voter-dashboard-container">
            {renderContents()}
        </div>
    );
}

export default VoterDashboard;
