import React, { useReducer } from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import AdminLogin from '../Admin/AdminLogin';
import AdminDashboard from '../Admin/AdminDashboard';
import VoterRegistration from '../Voter/VoterRegistration';
import VoterDashboard from '../Voter/VoterDashboard';
import { VoterReducer } from '../../VoterReducer';
import VoterContext from '../../VoterContext';

import '../../styles/App.css';

function App() {
  const initialVoterState = {
    elections: [],
    electionsLoaded: false,
    selectedElection: null
  };
  const [voterState, voterDispatch] = useReducer(VoterReducer, initialVoterState);

  return (
    <Router>
      {
        <Switch>
          <Route path="/admin/dashboard">
            <AdminDashboard username="Sample Username" organization="Sample Organization" />
          </Route>
          <Route path="/admin">
            <AdminLogin />
          </Route>
          <Route path="/voter/dashboard">
            <VoterContext.Provider value={{ voterState, voterDispatchÂ }}>
              <VoterDashboard
                elections={voterState.elections}
                electionsLoaded={voterState.electionsLoaded}
                selectedElection={voterState.selectedElection}
              />
            </VoterContext.Provider>
          </Route>
          <Route path="/">
            <VoterRegistration />
          </Route>
          {/* <Route path="/voter">
            <VoterRegistration />
          </Route>
          <Route path="/">
            <Login />
          </Route> */}
        </Switch>
      }
    </Router>
  );
}

export default App;
